import { ChangeEvent } from "react";
import { PaginationType } from "../../App";

type PaginationProps = {
  pagination: PaginationType;
  handlePageChange: (
    cb: (
      setPagination: React.Dispatch<React.SetStateAction<PaginationType>>
    ) => void
  ) => void;
  getTotalPage: (pagination: PaginationType) => number;
};

const pageSelector: number[] = [5, 10, 20, 25];

const Pagination: React.FC<PaginationProps> = ({
  pagination,
  handlePageChange,
  getTotalPage,
}) => {
  const { currentPage, totalPage, itemPerPage } = pagination;

  function currentPageChange(currentPage: number) {
    handlePageChange((setPagination) => {
      setPagination((pagination) => ({
        ...pagination,
        currentPage: currentPage,
      }));
    });
  }

  function itemPerPageChange(e: ChangeEvent<HTMLSelectElement>) {
    handlePageChange((setPagination) => {
      setPagination((pagination) => {
        const itemPerPage = Number(e.target.value);
        const totalPage = getTotalPage({ ...pagination, itemPerPage });

        return {
          currentPage: 0,
          totalPage,
          itemPerPage,
        };
      });
    });
  }

  function navigatePage(type: "next" | "previous") {
    handlePageChange((setPagination) => {
      setPagination((pagination) => {
        let currentPage =
          type === "next"
            ? pagination.currentPage + 1
            : pagination.currentPage - 1;

        if (currentPage < 0) {
          currentPage = pagination.totalPage - 1;
        }

        if (currentPage >= pagination.totalPage) {
          currentPage = 0;
        }

        return { ...pagination, currentPage };
      });
    });
  }
  return (
    <div className="pagination-section">
      <ul className="pagination">
        <li
          className="pagination__item nav"
          onClick={() => navigatePage("previous")}
        >
          Previous
        </li>

        {Array.from({ length: 7 }, (v, i) => i).map((index) => {
          const pivot = Math.floor(7 / 2);

          const isGreaterThanPivot = currentPage > pivot;
          const isLessThanPivot = currentPage < totalPage - pivot - 1;

          if (
            (isGreaterThanPivot && index === 1) ||
            (isLessThanPivot && index === 7 - 2)
          ) {
            return (
              <li className={`pagination__item`} key={index}>
                ...
              </li>
            );
          }

          if (index == 0) {
            return (
              <li
                className={`${
                  currentPage === 0 ? "active " : ""
                }pagination__item`}
                key={0}
                onClick={() => currentPageChange(0)}
              >
                {0}
              </li>
            );
          }

          if (isGreaterThanPivot && isLessThanPivot && index == 2) {
            return (
              <li
                className="pagination__item"
                key={index}
                onClick={() => currentPageChange(currentPage - 1)}
              >
                {currentPage - 1}
              </li>
            );
          }
          if (isGreaterThanPivot && isLessThanPivot && index == 3) {
            return (
              <li
                className="active pagination__item"
                key={index}
                onClick={() => currentPageChange(currentPage)}
              >
                {currentPage}
              </li>
            );
          }
          if (isGreaterThanPivot && isLessThanPivot && index == 4) {
            return (
              <li
                className="pagination__item"
                key={index}
                onClick={() => currentPageChange(currentPage + 1)}
              >
                {currentPage + 1}
              </li>
            );
          }

          if (index == 6) {
            return (
              <li
                className={`${
                  currentPage === totalPage  ? "active " : ""
                }pagination__item`}
                key={totalPage}
                onClick={() => currentPageChange(totalPage-1)}
              >
                {totalPage - 1}
              </li>
            );
          }

          if(currentPage > totalPage - pivot - 1 ){
            const idx = totalPage - pivot + index - 4;

            return (
              <li
                className={`${
                  currentPage === idx ? "active " : ""
                }pagination__item`}
                key={idx}
                onClick={() => currentPageChange(idx)}
              >
                {idx}
              </li>
            );
          }

          return (
            <li
              className={`${
                currentPage === index ? "active " : ""
              }pagination__item`}
              key={index}
              onClick={() => currentPageChange(index)}
            >
              {index}
            </li>
          );
          // }
        })}
        <li
          className="pagination__item nav"
          onClick={() => navigatePage("next")}
        >
          Next
        </li>
      </ul>
      <select
        className="item-per-page"
        name=""
        id=""
        onChange={(e) => itemPerPageChange(e)}
      >
        {pageSelector.map((item) => (
          <option value={item} selected={itemPerPage === item} key={item}>
            {item}
          </option>
        ))}
      </select>
      {JSON.stringify(pagination.currentPage)}
    </div>
  );
};

export default Pagination;
