import { useEffect, useState, useTransition } from "react";
import Pagination from "./components/Pagination";
import Card from "./components/Card";
import { MockData, promise, mocks } from "./mocks";

import "./styles.css";
export type PaginationType = {
  currentPage: number;
  totalPage: number;
  itemPerPage: number;
};

export default function App() {
  const [data, setData] = useState<Array<MockData>>([]);
  const [isLoading, setIsLoading] = useState<Boolean>();
  const [pagination, setPagination] = useState<PaginationType>({
    currentPage: 0,
    totalPage: 0,
    itemPerPage: 10,
  });

  function handlePageChange(
    cb: (
      setPagination: React.Dispatch<React.SetStateAction<PaginationType>>
    ) => void
  ): void {
    cb(setPagination);
  }

  function getTotalPage(pagination: PaginationType): number {
    const remainder = (mocks.length + 1) % pagination.itemPerPage;
    const quotient = Math.floor((mocks.length + 1) / pagination.itemPerPage);
    const totalPage = remainder == 0 ? quotient : quotient + 1;

    return totalPage;
  }

  async function fetchData() {
    setIsLoading(true);
    const { currentPage, itemPerPage } = pagination;
    const start = currentPage * itemPerPage;
    const end = start + itemPerPage + 1;
    const result = await promise;

    const data = result.slice(start, end);

    setPagination((pagination) => {
      const totalPage = getTotalPage(pagination);
      return {
        ...pagination,
        totalPage,
      };
    });

    setData(data);
    setIsLoading(false);
  }

  useEffect(() => {
    fetchData();
  }, [pagination.itemPerPage, pagination.currentPage]);

  return (
    <div className="App">
      <div className="content">
        <h1>Hello CodeSandbox</h1>
        <h2>Start editing to see some magic happen!</h2>

        <div className="main">
          {isLoading && <div>Loading...</div>}
          {data.length > 0 &&
            data.map((item) => {
              return <Card cardData={item} key={item.header} />;
            })}
        </div>
      </div>

      <Pagination
        pagination={pagination}
        handlePageChange={handlePageChange}
        getTotalPage={getTotalPage}
      />
    </div>
  );
}
